		speed = B4800;
		break;
	}
	case 9600:
	{
		speed = B9600;
		break;
	}
	case 19200:
	{
		speed = B19200;
		break;
	}
	case 38400:
	{
		speed = B38400;
		break;
	}
	case 115200:
	{
		speed = B115200;
		break;
	}


	}
	cfsetispeed(&new_cfg, speed);
	cfsetospeed(&new_cfg, speed);
	//è®¾ç½®æ•°æ®ä½

	switch (data_bits)
	{
	case 7:
	{
		new_cfg.c_cflag |= CS7;
		break;
	}

	case 8:
	{
		new_cfg.c_cflag |= CS8;
		break;
	}

	}

	//è®¾ç½®åœæ­¢ä½
	switch (stop_bits)
	{
	case 1:
	{
		new_cfg.c_cflag &= ~CSTOPB;
		break;
	}

	case 2:
	{
		new_cfg.c_cflag |= CSTOPB;
		break;
	}


	}

	//è®¾ç½®å¥‡å¶æ ¡éªŒä½
	switch (parity)
	{
	case 'o':
	case 'O':
	{
		new_cfg.c_cflag |= (PARODD | PARENB);
		new_cfg.c_iflag |= (INPCK | ISTRIP);
		break;
	}
	case 'e':
	case 'E':
	{
		new_cfg.c_cflag |= PARENB;
		new_cfg.c_cflag &= ~PARODD;
		new_cfg.c_iflag |= (INPCK | ISTRIP);
		break;
	}
	case 's':
	case 'S':
	{
		new_cfg.c_cflag &= ~PARENB;
		new_cfg.c_cflag &= ~CSTOPB;
		break;
	}

	case 'n':
	case 'N':
	{
		new_cfg.c_cflag &= ~PARENB;
		new_cfg.c_iflag &= ~INPCK;
		break;
	}

	}

	new_cfg.c_cc[VTIME] = 10;
	new_cfg.c_cc[VMIN] = 5;
	//å¤„ç†æœªæ¥æ”¶å­—ç¬¦
	tcflush(fd, TCIFLUSH);

	if ((tcsetattr(fd, TCSANOW, &new_cfg)) != 0)
	{
		perror("tcsetattr");
		return -1;
	}

	return 0;
}


