void GPS_resolve_GPRMC(char data)
{
//$GPRMC,092427.604,V,4002.1531,N,11618.3097,E,0.000,0.00,280812,,E,N*08


	if (data == ',')
	{
		++SectionID;
		i = 0;
	}
	else
	{
		switch (SectionID)
		{
		case 1://02:48:13
			GPS_DATA.GPS_time[i++] = data;
			if (i == 2 || i == 5)
			{
				GPS_DATA.GPS_time[i++] = ':';
			}
			GPS_DATA.GPS_time[8] = '\0';
			break;
		case 2:
			if (data == 'A')
				GPS_DATA.GPS_sv = '>';
			else
				GPS_DATA.GPS_sv = '<';
			break;
		case 3://3158.4608
			GPS_DATA.GPS_wd[++i] = data;
			GPS_DATA.GPS_wd[12] = '\0';
			break;

		case 4:
			if (data == 'N')
				GPS_DATA.GPS_wd[0] = 'N';
			else if (data == 'S')
				GPS_DATA.GPS_wd[0] = 'S';

			break;
		case 5://11848.3737,E

			GPS_DATA.GPS_jd[++i] = data;
			GPS_DATA.GPS_jd[12] = '\0';
			break;

		case 6:
			if (data == 'E')
				GPS_DATA.GPS_jd[0] = 'E';
			else if (data == 'W')
				GPS_DATA.GPS_jd[0] = 'W';

			break;
		case 7://10.05
			GPS_DATA.GPS_speed[i++] = data;
			GPS_DATA.GPS_speed[4] = '\0';
			break;
		case 9://15-07-06 -> 06-07-15
			GPS_DATA.GPS_date[i++] = data;
			if (i == 2 || i == 5)
			{
				GPS_DATA.GPS_date[i++] = '-';
			}
			GPS_DATA.GPS_date[8] = '\0';
			break;
		}
	}

}

